var link=document.querySelector(".btn__map"),popup=document.querySelector(".modal"),close=popup.querySelector(".modal__close"),form=popup.querySelector("form"),login=popup.querySelector("[name=name]");link.addEventListener("click",function(e){e.preventDefault(),popup.classList.add("modal__show"),login.focus()}),close.addEventListener("click",function(e){e.preventDefault(),popup.classList.remove("modal__show")}),form.addEventListener("submit",function(e){login.value||(e.preventDefault(),console.log("Нужно ввести логин и пароль"))}),window.addEventListener("keydown",function(e){27===e.keyCode&&popup.classList.contains("modal__show")&&popup.classList.remove("modal__show")});